import React from 'react';
import Container from '@material-ui/core/Container';
import SelectFileButton from './SelectFileButton';
import GenerateQuestions from './GenerateQuestions';
import Navbar from './Navbar';
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import { useState, useEffect } from 'react';
import { Button, TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';





function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="/home">
        Viktorina-AI
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

class Home extends React.Component
{
  constructor(){
    super();
    this.state = {
      items: [],
      isLoaded: false,
      value:'initial value is not good'
    };
    this.handleChange = this.handleChange.bind(this);

    const [expanded, setExpanded] = React.useState(false);

    const handleExpandClick = () => {
      setExpanded(!expanded);
    };
  }

  handleChange(event) {
    this.setState({value: event.target.value});  
  }
 

 generate() {
  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
    body: 'togenerate='+this.state.value
  };
  fetch('/generate-questions', requestOptions).then(response=>response.json()).then(data=>{this.setState({items:data,isLoaded:true});});
}

  useStyles = makeStyles((theme) => ({
  
    footer: {
      borderTop: `1px solid ${theme.palette.divider}`,
      marginTop: theme.spacing(8),
      paddingTop: theme.spacing(3),
      paddingBottom: theme.spacing(3),
      [theme.breakpoints.up('sm')]: {
        paddingTop: theme.spacing(6),
        paddingBottom: theme.spacing(6),
      },
    },root: {
      maxWidth: 345,
    },
    media: {
      height: 0,
      paddingTop: '56.25%', // 16:9
    },
    expand: {
      transform: 'rotate(0deg)',
      marginLeft: 'auto',
      transition: theme.transitions.create('transform', {
        duration: theme.transitions.duration.shortest,
      }),
    },
    expandOpen: {
      transform: 'rotate(180deg)',
    },
    avatar: {
      backgroundColor: red[500],
    }
  }));
  
   render(){
    const { items } = this.state;
  return (
    <div>
      
    <Navbar></Navbar>
    <Typography>
    <Container maxWidth="md" component="main">
    <h4>Convert your PDF course into questions that may be part of your next exam.<br></br>Good luck!</h4>
        <TextField id="pastedtext" name="togenerate" rows="10" placeholder="Paste your text here" onChange={this.handleChange} ></TextField>
        <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            onClick={this.generate.bind(this)}>
            Generate
          </Button>
          <div>
    <h3>Your questions and answers will appear below:</h3>
    <ul>
          {items.map(item => (
            <li key={item.id}>
              <h3>{item.answer}</h3>
              <p>{item.question}</p>
            </li>
          ))}
        </ul>
  </div>

  <Card className={classes.root}>
      <CardHeader
        title="Shrimp and Chorizo Paella"
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          <Card className={classes.root}>
      <CardHeader
        title="Shrimp and Chorizo Paella"
        subheader="September 14, 2016"
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
        {items.map(item => (
            <li key={item.id}>
              <h3>{item.answer}</h3>
              <p>{item.question}</p>
            </li>
          ))}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
       
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography paragraph>Method:</Typography>
          <Typography paragraph>
            Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
            minutes.
          </Typography>
        </CardContent>
      </Collapse>
    </Card>
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
       
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography paragraph>Method:</Typography>
          <Typography paragraph>
            Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
            minutes.
          </Typography>
        </CardContent>
      </Collapse>
    </Card>


    </Container>
    </Typography>
    <Container maxWidth="md" component="footer" >
      <Copyright />
      </Container>
    </div>
  );
  }
}

export default Home;
