import React from 'react';
import Container from '@material-ui/core/Container';
import SelectFileButton from './SelectFileButton';
import GenerateQuestions from './GenerateQuestions';
import Navbar from './Navbar';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import { useState, useEffect } from 'react';
import { Button } from '@material-ui/core';



function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="/home">
        Viktorina-AI
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

class Home extends React.Component
{
  constructor() {
    super();
    this.state = {user: " you are currently unauthenticated"};
  }
  useStyles = makeStyles((theme) => ({
  
    footer: {
      borderTop: `1px solid ${theme.palette.divider}`,
      marginTop: theme.spacing(8),
      paddingTop: theme.spacing(3),
      paddingBottom: theme.spacing(3),
      [theme.breakpoints.up('sm')]: {
        paddingTop: theme.spacing(6),
        paddingBottom: theme.spacing(6),
      },
    },
  }));
  

  // componentDidMount() {
  //   this.timer = setInterval(()=> alert("You should login to be able to continue"), 150000);
  // }
  

  getUsername() {

    // fetch("http://localhost:5000/signin", { 
    //   method: 'get',
      
    //   headers: {
    //     'Access-Control-Allow-Origin': 'http://localhost:3000/'
    //     ,'Accept': 'application/json',
    //     'Content-Type': ' application/json',
    //   },
    // }).then(result => result.json())
    //   .then(result => this.setState({ user: result }))
 
  }
  
  render(){
  return (
    <div>
      
    <Navbar></Navbar>
    <Typography>
    <Container maxWidth="md" component="main">
    <h4>Convert your PDF course into questions that may be part of your next exam.<br></br>Good luck!</h4>
            <p>Welcome,{this.state.user}.</p>    
                  <Container maxWidth="md" component="main">
        <form action='/generate-questions' method="post">
        <div class="form-group">
        <textarea class="form-control" id="pastedtext" rows="10" placeholder="Paste your text here"></textarea>
      </div>
    </form>
    </Container>  
    <Button>Generate Questions</Button>
    </Container>
    </Typography>
    <Container maxWidth="md" component="footer" >
      <Copyright />
      </Container>
    </div>
  );
  }
}
export default Home;
