import React from 'react';
import Container from '@material-ui/core/Container';
import SelectFileButton from './SelectFileButton';
import GenerateQuestions from './GenerateQuestions';
import Navbar from './Navbar';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import { useState, useEffect } from 'react';
import { Button } from '@material-ui/core';



function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="/home">
        Viktorina-AI
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

class Home extends React.Component
{
  constructor(){
    super();
    this.state = {quesans: []}
  }
  useStyles = makeStyles((theme) => ({
  
    footer: {
      borderTop: `1px solid ${theme.palette.divider}`,
      marginTop: theme.spacing(8),
      paddingTop: theme.spacing(3),
      paddingBottom: theme.spacing(3),
      [theme.breakpoints.up('sm')]: {
        paddingTop: theme.spacing(6),
        paddingBottom: theme.spacing(6),
      },
    },
  }));
  
  

getquestions(data){
  fetch('http://127.0.0.1:5000/generate-questions', {
  method: 'POST', // The method 
  mode: 'no-cors', // It can be no-cors, cors, same-origin
  credentials: 'same-origin', // It can be include, same-origin, omit
  headers: {
    'Content-Type': 'text/plain', // Your headers
    'Access-Control-Allow-Origin': '*',
  },
  body: data
})
      .then( response => response.json() )
    .then( result => this.setState({ quesans: result}))
    
}
 
  
  render(){
  return (
    <div>
      
    <Navbar></Navbar>
    <Typography>
    <Container maxWidth="md" component="main">
    <h4>Convert your PDF course into questions that may be part of your next exam.<br></br>Good luck!</h4>
               
                  <Container maxWidth="md" component="main">
        
        <textarea class="form-control" id="pastedtext" rows="10" placeholder="Paste your text here"></textarea>
        <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
          >
            Generate
          </Button>
      
    </Container> 
    </Container>
    </Typography>
    <Container maxWidth="md" component="footer" >
      <Copyright />
      </Container>
    </div>
  );
  }
}
export default Home;
