import React from 'react';
import Container from '@material-ui/core/Container';
import SelectFileButton from './SelectFileButton';
import GenerateQuestions from './GenerateQuestions';
import Navbar from './Navbar';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import { useState, useEffect } from 'react';
import { Button, TextField } from '@material-ui/core';



function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="/home">
        Viktorina-AI
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

class Home extends React.Component
{
  myres="raymonde"
  constructor(){
    super();
    this.state = {
      quesans: [{
          "answer": "Place holder ans 1",
          "question": "Place holder ques 1"
        },
        {
          "answer": "Place holder ans 2",
          "question": "Place holder ques 2"
        }
      ]
    };
  }

 async requ(){
  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
    body: 'togenerate=oliverBodyHome+est+mechant'
};
await fetch('/generate-questions', requestOptions).then(response=>response.json).then(data=>this.setState({"quesans":data}))
// const data = await response
// this.setState({ questions: data });
 }

  useStyles = makeStyles((theme) => ({
  
    footer: {
      borderTop: `1px solid ${theme.palette.divider}`,
      marginTop: theme.spacing(8),
      paddingTop: theme.spacing(3),
      paddingBottom: theme.spacing(3),
      [theme.breakpoints.up('sm')]: {
        paddingTop: theme.spacing(6),
        paddingBottom: theme.spacing(6),
      },
    },
  }));
  
  async render(){
    await this.requ();
    const { items } = this.state;
  return (
    <div>
      
    <Navbar></Navbar>
    <Typography>
    <Container maxWidth="md" component="main">
    <h4>Convert your PDF course into questions that may be part of your next exam.<br></br>Good luck!</h4>
    <p>{this.myres}</p>
    <div>
    <h3>Using local JSON file Array</h3>
                    <ul>
                    {items.map((item, i) => (
                      <tr key={i}>
                        <td>{item.answer}</td>
                        <td>{item.question}</td>
                      </tr>
                        ))}
                    </ul>
  </div>

        <TextField id="pastedtext" name="togenerate" rows="10" placeholder="Paste your text here"></TextField>
        <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            onClick={this.requ}
          >
            Generate
          </Button>
  
    </Container>
    </Typography>
    <Container maxWidth="md" component="footer" >
      <Copyright />
      </Container>
    </div>
  );
  }
}
export default Home;
