import React from 'react';
import Container from '@material-ui/core/Container';
import SelectFileButton from './SelectFileButton';
import GenerateQuestions from './GenerateQuestions';
import Navbar from './Navbar';
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import { useState, useEffect } from 'react';
import { Button, TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from "@material-ui/core/styles";
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';




function showAnswers(){
  document.getElementById('Show').style.display='none';
  document.getElementById('Hide').style.display='block'; 
  document.getElementById('SectionName').style.display='block'
}

function hideAnswers(){
  document.getElementById('Show').style.display='block'; 
  document.getElementById('Hide').style.display='none'; 
  document.getElementById('SectionName').style.display='none'
}

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="/home">
        Viktorina-AI
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const styles = theme => ({
    footer: {
    borderTop: `1px solid ${theme.palette.divider}`,
    marginTop: theme.spacing(8),
    paddingTop: theme.spacing(3),
    paddingBottom: theme.spacing(3),
    [theme.breakpoints.up('sm')]: {
      paddingTop: theme.spacing(6),
      paddingBottom: theme.spacing(6),
    },
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});


class Home extends React.Component
{
  constructor(){
    super();
    this.state = {
      items: [],
      isLoaded: false,
      value:'initial value is not good'
    };
    this.handleChange = this.handleChange.bind(this);

   // const [expanded, setExpanded] = React.useState(false); 
}
handleChange(event) {
  this.setState({value: event.target.value});  
}
 

 generate() {
  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
    body: 'togenerate='+this.state.value
  };
  fetch('/generate-questions', requestOptions).then(response=>response.json()).then(data=>{this.setState({items:data,isLoaded:true});});
}
  
   render(){
    const { items } = this.state;
    const { classes } = this.props;
  return (
    <div>
      
    <Navbar></Navbar>
    <Typography>
    <Container maxWidth="md" component="main">
    <h4>Convert your PDF course into questions that may be part of your next exam.<br></br>Good luck!</h4>
        <textarea class="form-control" id="pastedtext" name="togenerate" rows="10" placeholder="Paste your text here" onChange={this.handleChange} ></textarea>
        <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            onClick={this.generate.bind(this)}>
            Generate
          </Button>
          <div>
            <hr></hr>
    <h3>Your questions and answers will appear below:</h3>
    
  </div>

  {items.map(item => {
  return <Card className={classes.root}>
  <CardContent>   
    <Typography variant="h5" component="h2">
      Question
      
    </Typography>
    
    <Typography variant="body2" component="p">
    {item.question}
    </Typography>
    <div ID="SectionName" STYLE="display:none">{item.answer}</div>
  </CardContent>
  <CardActions>
  <button id="Show" 
          value="Click to show"
          style="display:block" 
          onClick={showAnswers}>Show answer</button>

  <button id="Hide" 
  value="Click to hide"
  style="display:none" 
  onClick={hideAnswers}>Hide answer</button>

  </CardActions>
</Card>
})}
  

    </Container>
    </Typography>
    <Container maxWidth="md" component="footer" >
      <Copyright />
      </Container>
    </div>
  );
  }
}

//export default Home;

export default withStyles(styles, { withTheme: true })(Home);

